import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Dashboard({ token, setToken }) {
  const [videoUrl, setVideoUrl] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [videos, setVideos] = useState([]);

  useEffect(() => {
    fetchUserVideos();
  }, []);

  const fetchUserVideos = async () => {
    try {
      const response = await axios.get('http://localhost:5000/my-videos', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setVideos(response.data.videos);
    } catch (err) {
      console.error('Failed to fetch videos');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResult(null);

    try {
      const response = await axios.post(
        'http://localhost:5000/process-video',
        { video_url: videoUrl },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setResult(response.data);
      setVideoUrl('');
      fetchUserVideos(); // Refresh the list
    } catch (err) {
      setError(err.response?.data?.error || 'Processing failed');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    setToken(null);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <h1 className="text-3xl font-bold text-gray-900">🎬 Video to Text Converter</h1>
            <button
              onClick={handleLogout}
              className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
            >
              Logout
            </button>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          
          {/* Video Processing Form */}
          <div className="bg-white overflow-hidden shadow rounded-lg mb-8">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                Process New Video
              </h3>
              <form onSubmit={handleSubmit}>
                <div className="mb-4">
                  <input
                    type="text"
                    placeholder="Enter YouTube URL or Video ID"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    value={videoUrl}
                    onChange={(e) => setVideoUrl(e.target.value)}
                    required
                  />
                </div>
                <button
                  type="submit"
                  disabled={loading}
                  className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded disabled:opacity-50"
                >
                  {loading ? 'Processing... This may take a few minutes' : 'Process Video'}
                </button>
              </form>

              {error && (
                <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-md">
                  <p className="text-red-600">{error}</p>
                </div>
              )}
            </div>
          </div>

          {/* Processing Result */}
          {result && (
            <div className="bg-white overflow-hidden shadow rounded-lg mb-8">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                  ✅ {result.video_title}
                </h3>
                
                <div className="space-y-6">
                  <div>
                    <h4 className="text-md font-medium text-gray-900 mb-2">📄 Transcript</h4>
                    <div className="bg-gray-50 p-4 rounded-md max-h-60 overflow-y-auto">
                      <p className="text-sm text-gray-700 whitespace-pre-wrap">{result.transcript}</p>
                    </div>
                  </div>
                  
                  <div>
                    <h4 className="text-md font-medium text-gray-900 mb-2">📝 Summary</h4>
                    <div className="bg-gray-50 p-4 rounded-md max-h-60 overflow-y-auto">
                      <p className="text-sm text-gray-700 whitespace-pre-wrap">{result.summary}</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Previous Videos */}
          <div className="bg-white overflow-hidden shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                📁 Your Previous Videos ({videos.length})
              </h3>
              
              {videos.length === 0 ? (
                <p className="text-gray-500">No videos processed yet.</p>
              ) : (
                <div className="space-y-4">
                  {videos.map((video, index) => (
                    <div key={index} className="border border-gray-200 rounded-lg p-4">
                      <div className="flex justify-between items-start mb-2">
                        <h4 className="font-medium text-gray-900">{video.title}</h4>
                        <span className="text-sm text-gray-500">{new Date(video.date).toLocaleDateString()}</span>
                      </div>
                      
                      <div className="space-y-3">
                        <div>
                          <p className="text-sm font-medium text-gray-700">Transcript:</p>
                          <p className="text-sm text-gray-600 bg-gray-50 p-2 rounded mt-1">
                            {video.transcript.substring(0, 200)}...
                          </p>
                        </div>
                        
                        <div>
                          <p className="text-sm font-medium text-gray-700">Summary:</p>
                          <p className="text-sm text-gray-600 bg-gray-50 p-2 rounded mt-1">
                            {video.summary.substring(0, 200)}...
                          </p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

        </div>
      </div>
    </div>
  );
}

export default Dashboard;
